package main

import (
	"fmt"
)

func main() {
	fmt.Println(beautifulPartitions2("23542185131", 3, 2))
	fmt.Println(beautifulPartitions("23542185131", 3, 2))
	fmt.Println(beautifulPartitions("", 500, 1))
	fmt.Println(beautifulPartitions2("", 500, 1))
}

// 还超时,要剪枝才得行
func beautifulPartitions(s string, k int, m int) int {
	n := len(s)
	ss := []byte(s)
	f := make([][]int, n+1)
	for i := range f {
		f[i] = make([]int, k+1)
	}
	// 初值
	f[0][0] = 1
	mod := int(1e9 + 7)

	for i := 1; i <= n; i++ {
		for j := i; j >= 0; j-- {
			for sp := 1; sp <= k; sp++ {
				if check(ss[j:i], m) {
					f[i][sp] += f[j][sp-1]
					f[i][sp] %= mod
				}
			}
		}
	}

	return f[n][k]
}

